import { Component, OnInit, OnDestroy } from '@angular/core';
// import { AuthService } from 'src/app/service/auth.service';
import { UsersService } from 'src/app/service/users.service';
import {Router} from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: 'change-password.component.html',
  styleUrls: ['./change-password.component.scss']
})
export class ChangePasswordComponent implements OnInit {

  userDetails:any;
  userId:any;
  showAlert = false;
  alertMsg = 'Please wait! we are logging you in.'
  alertColor = 'info'
  inSubmission = false

    credentials = {
      old_pass: '',
      new_pass: ''
    }

    constructor( 
      // private auth:AuthService, 
      public router: Router,
      private users: UsersService
      ) { }

      ngOnInit() {
        window.alert('on init')

        const storedUserDetails = localStorage.getItem('userDetails');


        if (storedUserDetails) {
          // Parse the storedUserDetails JSON string to an object
          this.userDetails = JSON.parse(storedUserDetails);
          console.log('change pass:', this.userDetails);
        } else {
          console.log('User details not found in localStorage.');
        }

        window.alert(this.userDetails.id)

        this.userId = this.userDetails.id
        // this.UserProfile.getSingleUser(this.userDetails.id).subscribe(
        //   (res:any)=>{
        //     console.log(res + 'single user')
        //     this.displayDtls = res.data
        //   }
        // )


      }

    async changePassword(){
      this.showAlert = true;
      console.log(this.credentials)

      setTimeout(() => {
      this.showAlert = true
      this.alertMsg = 'Loading... If sync persists check network'
      this.alertColor = 'info'
     try {   
      this.users.changePassword(this.credentials, this.userId).subscribe( 
        (res:any) => {
          console.log(res.data)
          // let {message} = res;
          // console.log(message)
          if(res.code == "100"){
            this.alertMsg = res.message
            this.alertColor = 'danger'
            this.inSubmission = false
          } 
          else if(res.code == "200"){
            this.alertMsg = "Password Changed succesfully"
            this.alertColor = "success"
            const {token} = res.data;
            const data = res.data;
            console.log(token);

            setTimeout(() => {
            localStorage.clear()
            this.router.navigate(['/login']) 
          }, 1600)
          }

        }
      );

    }
    catch(e){
    }
      }, 1600)
    }


}
<app-alert *ngIf="showAlert" [color]="alertColor">
  {{ alertMsg }}
</app-alert>
<div class="header bg-gradient-primary py-7 py-lg-8">
  <div class="container">
    <div class="header-body text-center mb-7">
      <div class="row justify-content-center">
        <div class="col-lg-5 col-md-6">
          <h1 class="text-white">Change Password!</h1>
          <!-- <p class="text-lead text-light">Login in as an admin to view the entire admin panel </p> -->
        </div>
      </div>
    </div>
  </div>
  <div class="separator separator-bottom separator-skew zindex-100">
    <svg x="0" y="0" viewBox="0 0 2560 100" preserveAspectRatio="none" version="1.1" xmlns="http://www.w3.org/2000/svg">
      <polygon class="fill-default" points="2560 0 2560 100 0 100"></polygon>
    </svg>
  </div>
</div>
<!-- Page content -->
<div class="container mt--8 pb-5">
  <div class="row justify-content-center">
    <div class="col-lg-5 col-md-7">
      <div class="card bg-white shadow border-0">
        <div class="card-body px-lg-5 py-lg-5">
          <div class="text-center text-muted mb-4">
            <small>Change Password</small>
          </div>
          <form role="form" #changePasswordForm="ngForm" (ngSubmit)="changePassword()">
            <div class="form-group">
              <div class="input-group input-group-alternative">
                <div class="input-group-prepend">
                  <span class="input-group-text"><i class="fa-solid fa-lock text-primary"></i></span>
                </div>
                <!-- <input class="form-control" placeholder="Password" type="password"> -->
                <input class="form-control" #old_pass="ngModel" 
                  type="password" name="old_password" [(ngModel)]="credentials.old_pass"
                  placeholder="Enter Your Password" required pattern="^[a-zA - Z0-9]*$"
                    />
              </div>
              <p *ngIf="old_pass.errors && old_pass.touched && old_pass.dirty" class="form-error">
                Password must have atleast a letter and a number
            </div>
            <div class="form-group">
              <div class="input-group input-group-alternative">
                <div class="input-group-prepend">
                  <span class="input-group-text"><i class="fa-solid fa-lock text-primary"></i></span>
                </div>
                <!-- <input class="form-control" placeholder="Password" type="password"> -->
                <input class="form-control" #new_pass="ngModel" 
                  type="password" name="new_password" [(ngModel)]="credentials.new_pass"
                  placeholder="Enter New Password" required pattern="^[a-zA - Z0-9]*$"
                    />
              </div>
              <p *ngIf="new_pass.errors && new_pass.touched && new_pass.dirty" class="form-error">
                Password must have atleast a letter and a number
            </div>
            <div class="custom-control custom-control-alternative custom-checkbox">
              <input class="custom-control-input" id=" customCheckLogin" type="checkbox">
              <label class="custom-control-label" for=" customCheckLogin">
                <span class="text-muted">Remember me</span>
              </label>
            </div>
            <div class="text-center">
              <button type="submit" class="btn btn-primary my-4"

              >
              Change Passsword
            </button>
            </div>
          </form>
        </div>
      </div>
      <div class="row mt-3">
        <div class="col-6">
          <a  routerLink="/forget-password" class="text-light"><small>Forgot password?</small></a>
        </div>
      </div>
    </div>
  </div>
</div>
